import * as actions from '../../../Store/Actions/Index'; 
import {Redirect} from 'react-router-dom';
import Input from '../../../Components/UI/Input/input';
import Navbar from '../../../Components/Navbar/Navbar';
import Sidebar from '../../../Components/Sidebar/Sidebar';
import React, { Component, useState, useEffect } from 'react';
import { connect } from 'react-redux';
import FormData from 'form-data'; 
import OneRole from '../../../Components/OneRole/OneRole';
import classes from './CreateOrg.css';
const CreateOrgs = props =>{
    //Input Element States
   const [createOrgForm,SetcreateOrgForm]= useState({
    name: {
        elementType: 'input',
        elementConfig: {
            className:'formcontrol',
            type: 'text',
            placeholder: 'Organization Name'
        },
        value: '',
        validation: {
            required: true,
        },
        valid: false,
        touched: false
    },
    email: {
        elementType: 'input',
        elementConfig: {
            className:'formcontrol',
            type: 'text',
            placeholder: 'Username'
        },
        value: '',
        validation: {
            required: true,
            isEmail: false
        },
        valid: false,
        touched: false
    },
    Owner: {
        elementType: 'dropdown',
        elementConfig: {
            className:'formcontrol',
            options:[]
        },
        value: '',
        valid: false,
        touched: false
    },
    Users: {
        elementType: 'select-multiple',
        elementConfig: {
            className:'formcontrol',
            options:[]
        },
        value: '',
        valid: false,
        touched: false
    },
    permisson_loaded:{
        label: 'Permission Loaded',
        elementType: 'check-box',
        value:false
    }
   })
   //To load users
    useEffect (()=>{
        let token = localStorage.getItem('token')
        props.onFetchAllUsers(token);
    },[]);

    //To check Validity
    const checkValidity=(value, rules) =>{
        let isValid = true;
        if (!rules) {
            return true;
        }

        if (rules.required) {
            isValid = value.trim() !== '' && isValid;
        }

        if (rules.minLength) {
            isValid = value.length >= rules.minLength && isValid
        }

        if (rules.maxLength) {
            isValid = value.length <= rules.maxLength && isValid
        }

        if (rules.isEmail) {
            const pattern = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
            isValid = pattern.test(value) && isValid
        }

        if (rules.isNumeric) {
            const pattern = /^\d+$/;
            isValid = pattern.test(value) && isValid
        }

        return isValid;
    }
    //Input Change Handler
    const inputChangedHandler = (event, controlName) => {
        const updatedControls = {
            ...createOrgForm,
            [controlName]: {
                ...createOrgForm[controlName],
                value: event.target.value,
                valid: checkValidity(event.target.value, createOrgForm[controlName].validation),
                touched: true
            }
        };
        SetcreateOrgForm(updatedControls);
    }
    const formElementsArray = []; //array for input elements
        for ( let key in createOrgForm ) {  // render input elements from array
            formElementsArray.push( {
                id: key,
                config: createOrgForm[key]
            });
        }
        let form = formElementsArray.map( formElement => (
            <Input
                key={formElement.id}
                className={formElement.config.className}
                elementType={formElement.config.elementType}
                elementConfig={formElement.config.elementConfig}
                value={formElement.config.value}
                invalid={!formElement.config.valid}
                shouldValidate={formElement.config.validation}
                touched={formElement.config.touched}
                changed={( event ) => inputChangedHandler( event, formElement.id )} />
        ) );

    let sidebar = <Sidebar/>;
    let navbar =  <Navbar name ={localStorage.getItem('name')}/>;
    let count = 0;
    //let rolesElement=  <Sidebar/>;
    /*
    if(!props.loading){

        rolesElement= props.AllRoles.map(event =>(
        <div class="reposition">
        <button name="componentButton" value={event}>
        <OneRole 
            name={event.name}
            permission={event.permissions}
            roleId = {event.id}
              />
              {console.log(event.permissions)}
        </button>
        </div>
           ))
    }*/
    return(
        <div>
            {navbar}
            {sidebar}
            <div class="fund-pics row">
                {form}
            </div>
    </div>)

    }



const mapAllOrganizationsDispatchToProps =dispatch => {

    return{
        onFetchAllUsers:(token) =>dispatch(actions.fetchAllUsers(token)),
        //onFetchOneEvents:(OrganizationId)=>dispatch(actions.fetchOneOrganizations(fundId))
    };
}


//to access the errors and loading state 
const mapStatetoProps = state =>{
    console.log('main page vwrole',state)
    return {
    AllRoles: state.AllRoles.AllRoles,
    loading:state.organizations.loading,
    token:state.auth.token
    };
};


export default connect(mapStatetoProps, mapAllOrganizationsDispatchToProps)(CreateOrgs);
