            required: true,
            isEmail: false
        },
        valid: false,
        touched: false
    },
    Owner: {
        elementType: 'select',
        elementConfig: {
            className:'formcontrol',
            options:''
        },
        value: '',
        valid: false,
        touched: false
    },
    Users: {
        elementType: 'select',
        elementConfig: {
            className:'formcontrol',
            options:''
        },
        value: '',
        valid: false,
        touched: false
    },
   })
    useEffect (()=>{
        let token = localStorage.getItem('token')
        props.onFetchAllUsers(token);
