import * as actions from '../../../Store/Actions/Index'; 
import {Redirect} from 'react-router-dom';
import Navbar from '../../../Components/Navbar/Navbar';
import Sidebar from '../../../Components/Sidebar/Sidebar';
import React, { Component, useState, useEffect } from 'react';
import { connect } from 'react-redux';
import FormData from 'form-data'; 

const ViewOrgs = props =>{
    const[gotOrganizations, setGotOrganizations] = useState([])



    useEffect (()=>{
        let token = localStorage.getItem('token')
        props.onFetchOrganizations(token);

    },[]);

    let sidebar = <Sidebar/>;
    let navbar =  <Navbar name ={localStorage.getItem('name')}/>;
    let count = 0;
    let orgzs=  <Sidebar/>;

    if (!props.loading) {
        let organizationsArr= props.organizations;
        let newArr=[];
        console.log("LENGTHHH",organizationsArr.length);
    if(organizationsArr.length > 0){
            for (let i=0;i<organizationsArr.length;i++){
                console.log(i);
                newArr.push({
                    name: organizationsArr[i].name,
                    owner: organizationsArr[i].owner,
                    OrgId: organizationsArr[i].id
                });
            setGotOrganizations(newArr);
            console.log('EVENT NAME',gotOrganizations);

            }
    }
            return orgzs = gotOrganizations.map( events => (
                    <button name="componentButton" >
                    <div className="col-md-6 col-lg-4 col-xl-3">
                    <figure className="snip1527" >
                        <div className="image"><img src={require("./new_img.jpg")}/></div>
                        <figcaption>
                        <h3>NAME: {events.name}</h3>
                        <h3>OWNER: {events.owner}</h3>
                        <h3>Organization ID :{events.id}</h3>
                        </figcaption>
                        <a ></a>
                    </figure>
                    </div>
                    </button>
                    ))

    }

    return(
        <div className="myfunds">
            {navbar}
            {sidebar}
            {orgzs}
        </div>

    )

    }



const mapAllOrganizationsDispatchToProps =dispatch => {

    return{
        onFetchOrganizations:(token) =>dispatch(actions.fetchOrganizations(token)),
        //onFetchOneEvents:(OrganizationId)=>dispatch(actions.fetchOneOrganizations(fundId))
    };
}


//to access the errors and loading state 
const mapStatetoProps = state =>{
    console.log('main page vworg',state)
    return {
    organizations: state.organizations.organizations,
    loading:state.organizations.loading,
    token:state.auth.token
    };
};


export default connect(mapStatetoProps, mapAllOrganizationsDispatchToProps)(ViewOrgs);
